<?php

namespace AppBundle\Repository;

use AppBundle\Entity\Customer;
use AppBundle\Helper\Traits\RepositoryOrdeByTrait;
use AppBundle\Helper\Traits\RepositoryPaginationTrait;
use Doctrine\ORM\EntityRepository;
use Doctrine\ORM\NonUniqueResultException;
use Doctrine\ORM\NoResultException;

/**
 * CustomerRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class CustomerRepository  extends EntityRepository
{
    use RepositoryPaginationTrait;
    use RepositoryOrdeByTrait;

    /**
     * @param array|null $orderBy
     * @param null $limit
     * @param int $offset
     * @return mixed
     */
    public function findCustomers(array $orderBy = null, $limit = null, $offset = 0)
    {
        $query = $this->getEntityManager()->createQueryBuilder()
            ->select('c')
            ->from('AppBundle:Customer', 'c')
        ;
        $this->initOrderBy($query, $orderBy);
        $this->initPagination($query, $limit, $offset);
        return $query->getQuery()->getResult();

    }

    /**
     * @param $id
     * @return Customer
     * @throws NonUniqueResultException
     */
    public function findCustomerById($id)
    {
        $query = $this->getEntityManager()->createQueryBuilder()
            ->select('c')
            ->from('AppBundle:Customer', 'c')
            ->where('c.id=:id')
            ->setParameter('id',$id)
        ;
        return $query->getQuery()->getOneOrNullResult();
    }

    /**
     * @return int
     * @throws NoResultException
     * @throws NonUniqueResultException
     */
    public function countCustomer(){
        $query = $this->getEntityManager()->createQueryBuilder()
            ->select('COUNT(c.id)')
            ->from('AppBundle:Customer', 'c')
        ;

        return $query->getQuery()->getSingleScalarResult();
    }
}
